## PURPOSE:  PEPFAR - Country Focus Areas
## Date:     2022-09-23

# LIBRARIES ----

  library(tidyverse)
  library(gophr)
  library(grabr)
  library(gisr)
  library(glitr)
  library(glamr)
  library(janitor)
  library(scales)
  library(patchwork)
  library(extrafont)

# SETUP ----

  ## Directories ----
  dir_data <- "Data"
  dir_dataout <- "Dataout"
  dir_gis <- "GIS"
  dir_graphics <- "Graphics"

  dir_geodata <- si_path("path_vector")
  dir_terr <- si_path("path_raster")
  dir_merdata <- si_path("path_msd")

  ## Reporting Filters ----
  rep_agency = "USAID"

  ## Disaggs
  disaggs <- list(
    tn = "Total Numerator",
    td = "Total Denominator",
    as = "Age/Sex",
    kp = "KeyPop"
  )

  ## Files ----

  ## Global PSNUxIM
  file_psnu_im <- glamr::return_latest(
    folderpath = dir_merdata,
    pattern = "PSNU_IM_FY20-23_.*.zip$"
  )

  ## Data Sources
  msd_source <- file_psnu_im %>% source_info()

  curr_fy <- msd_source %>%
    str_extract("(?<=FY).*(?=Q)") %>%
    paste0("20", .)

# LOAD DATA ----

  ## Geodata
  ras <- get_raster()
  spdf_pepfar <- get_vcpolygons()

  df_attrs <- cntries %>%
    map_dfr(get_attributes)

  spdf_nga <- spdf_pepfar %>%
    dplyr::left_join(df_attr, by = c("uid" = "id"))

  # Country sub-units
  spdf_cntry <- spdf_nga %>% filter(label == "country")
  spdf_psnu <- spdf_nga %>% filter(label == "prioritization")
  spdf_lga <- spdf_nga %>% filter(label == "community")

  # Global PSNUxIM Dataset
  df_psnu <- file_psnu_im %>%
    read_msd() %>%
    filter(fiscal_year == curr_fy,
           funding_agency == rep_agency)

# MUNGING ----

  df_cov <- df_psnu %>%
    distinct(operatingunituid, operatingunit, country, psnuuid, psnu)

  cntries <- df_cov %>%
    filter(str_detect(operatingunit, " Region$", negate = TRUE)) %>%
    distinct(country) %>%
    pull()

  gh_cntries <- c("Ghana", "Kenya", "Uganda")

  df_fareas <- gh_cntries %>%
    map_dfr(function(.cntry) {
      df_cov %>% filter(country == .cntry)
    }) %>%
    select(!ends_with("uid"))

  write_csv(x = df_fareas, na = "",
            file = file.path(dir_dataout,
                             paste0("PEPFAR - Focus Areas (as of ",
                                    msd_source,
                                    ") ",
                                    curr_date(),
                                    ".csv")))

  # Attributes
  df_attrs <- cntries %>%
    map_dfr(~get_attributes(country = .x))

  spdf <- spdf_pepfar %>%
    dplyr::left_join(df_attrs, by = c("uid" = "id"))


