                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                        ---

  #cntries %>%
  gh_cntries %>%
    #first() %>%
    walk(function(.cntry) {
      # info
      logger::log_info(.cntry)

      df_areas <- df_cov %>%
        filter(country == .cntry)

      # Country sub-units
      spdf_cntry <- spdf %>%
        filter(countryname == .cntry, label == "country")

      spdf_snu1 <- spdf %>%
        filter(countryname == .cntry, label == "snu1")

      spdf_psnu <- spdf %>%
        filter(countryname == .cntry, label == "prioritization") %>%
        clean_column(colname = "name")

      spdf_psnu2 <- spdf %>%
        filter(countryname == .cntry, label == "prioritization") %>%
        clean_column(colname = "name") %>%
        left_join(df_areas, by = c("uid" = "psnuuid")) %>%
        filter(!is.na(psnu))

      # Viz
      basemap <- terrain_map(countries = spdf_cntry,
                             adm0 = spdf_cntry,
                             adm1 = spdf_snu1,
                             terr = ras,
                             mask = TRUE)

      map <- basemap +
        geom_sf(data = spdf_psnu2,
                fill = burnt_sienna, alpha = .7,
                size = .2, color = grey10k) +
        geom_sf_text(data = spdf_psnu2,
                     aes(label = str_replace(name, " ", "\n")),
                     size = 1.5, color = usaid_darkgrey) +
        geom_sf(data = spdf_cntry,
                fill = NA, alpha = .7,
                size = 1, color = grey10k) +
        geom_sf(data = spdf_cntry,
                fill = NA, alpha = .7,
                size = .3, color = grey90k) +
        labs(x = NULL, y = NULL) +
        si_style_map() +
        theme(axis.text.x = element_blank(),
              axis.text.y = element_blank())

      print(map)

      file_output <- .cntry %>%
        paste0("USAID-", .,
               " - PEPFAR Focus Areas (as of ",
               msd_source,
               ") ",
               curr_date(), ".png") %>%
        file.path(dir_graphics, .)

      si_save(filename = file_output, plot = map)
    })


